mapper.select:
SELECT T1.PERSON_ID AS personId, substr( t1.grade_name, instr( t1.grade_name, '.' ) + 1 ) AS gradeName, t1.people_group AS peopleGroup, T1.FULL_NAME AS fullName, T1.EMPLOYEE_NUMBER AS employeeNumber, t1.national_identifier AS nationalIdentifier, T1.SEX AS sex, IF ( DATE_FORMAT ( T1.DATE_OF_BIRTH, '%Y-%m-%d' ) = '0000-00-00', '', DATE_FORMAT ( T1.DATE_OF_BIRTH, '%Y-%m-%d' ) ) AS dateOfBirth, TIMESTAMPDIFF( YEAR, t1.date_of_birth, CURDATE( ) ) AS age, IF ( DATE_FORMAT ( T1.INITIAL_JOB_START_DATE, '%Y-%m-%d' ) = '0000-00-00', '', DATE_FORMAT ( T1.INITIAL_JOB_START_DATE, '%Y-%m-%d' ) ) AS initialJobStartDate, t1.job_name AS jobName, eoa.NAME AS orgName, ( CASE
WHEN eoa.ORG2 != '42125' THEN eoa.NAME WHEN eoa.ORG2 = '42125' THEN SUBSTR( eoa.NAME, INSTR( eoa.NAME, '-' ) + 1, LENGTH( eoa.NAME ) ) END ) AS attribute1, eoa.organization_id AS orgId, t1.education_experience AS educationExperience, t1.degree AS degree, IF ( DATE_FORMAT ( T1.JOIN_CUC_DATE, '%Y-%m-%d' ) = '0000-00-00', '', DATE_FORMAT ( T1.JOIN_CUC_DATE, '%Y-%m-%d' ) ) AS joinCucDate, T1.USER_PERSON_TYPE AS userPersonType, (SELECT meaning FROM hades_lookups WHERE hades_lookups.lookup_type ='CN_HUKOU_TYPE' AND LOOKUP_CODE = T1.HOUSEHOLD_REGISTER_TYPE ) AS householdRegisterType, t1.staff_category AS staffCategory, (SELECT meaning FROM hades_lookups WHERE hades_lookups.lookup_type ='CN_HUKOU_LOCN' AND LOOKUP_CODE = T1.household_register_place) AS householdRegisterPlace, T1.JOIN_CUC_CHANNEL AS joinCucChannel, t1.assignment_category AS assignmentCategory, ( SELECT MEANING FROM hades_lookups WHERE hades_lookups.lookup_type = 'CN_PARTY_TYPE' AND LOOKUP_CODE = T1.politics_landscape_name ) AS politicsLandscapeName, ( SELECT MEANING FROM hades_lookups WHERE hades_lookups.lookup_type = 'CN_RACE' AND LOOKUP_CODE = T1.nation ) AS nation FROM EHRBASE_EMP_BASIC T1 INNER JOIN EHRBASE_ORGANIZATION_ALL eoa ON t1.org_id = eoa.ORGANIZATION_ID and eoa.BUSINESS_GROUP_ID = 101 WHERE 1 = 1
AND t1.employee_number like concat(#{employeeNumber},'%')
AND t1.full_name like concat(#{ fullName },'%')
AND t1.org_id = #{ org_id }
AND t1.user_person_type = #{userPersonType}

SELECT COUNT(1) as total FROM EHRBASE_EMP_BASIC T1 INNER JOIN EHRBASE_ORGANIZATION_ALL eoa ON t1.org_id = eoa.ORGANIZATION_ID and eoa.BUSINESS_GROUP_ID = 101 WHERE 1 = 1
AND t1.employee_number like concat(#{employeeNumber},'%')
AND t1.full_name like concat(#{ fullName },'%')
AND t1.org_id = #{ org_id }
AND t1.user_person_type = #{userPersonType}

SELECT t1.name_of_letters, t1.user_person_type, t1.national_identifier, DATE_FORMAT( t1.date_of_birth, '%Y-%m-%d' ) AS date_of_birth, CASE WHEN t1.sex = 'M' THEN '男性' WHEN t1.sex = 'F' THEN '女性' ELSE '' END AS sex, t1.blood_type, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'NATIONALITY' AND lookup_code = t1.nationality ) nationality, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CN_RACE' AND lookup_code = t1.nation ) nation, t1.town_of_birth, t1.region_of_birth, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CN_HUKOU_TYPE' AND lookup_code = t1.household_register_type ) household_register_type, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CN_HUKOU_LOCN' AND lookup_code = t1.household_register_place ) household_register_place, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'MAR_STATUS' AND lookup_code = t1.marital_status ) marital_status, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CN_HEALTH_STATUS' AND lookup_code = t1.state_of_health ) state_of_health, t1.email_address, t1.social_security_num, '' enjoy_tax_perfer, DATE_FORMAT( t1.initial_job_start_date, '%Y-%m-%d' ) initial_job_start_date, DATE_FORMAT( t1.join_cuc_date, '%Y-%m-%d' ) join_cuc_date, t1.join_cuc_channel, t1.join_cuc_other_channel, t1.specialties, DATE_FORMAT( t1.plan_of_retirement_date, '%Y-%m-%d' ) plan_of_retirement_date, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_OTHER_PER_TYPE' AND lookup_code = t1.other_user_person_type ) other_user_person_type, t1.join_cuc_other_channel_new, t1.join_cuc_channel_new, t1.join_cuc_channel_mark_new, DATE_FORMAT( t1.working_ages_start_date, '%Y-%m-%d' ), t1.interval_working_ages_month, DATE_FORMAT( t1.join_office_date, '%Y-%m-%d' ), DATE_FORMAT( t1.add_staff_date, '%Y-%m-%d' ) add_staff_date, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_ADD_WAY' AND lookup_code = t1.add_staff_type ) add_staff_type, t1.add_staff_comments, t1.staff_sort_num, t1.ufida_system_number FROM ehrbase_emp_basic t1 where 1 = 1
and t1.person_id = #{personId}
and 1=2

SELECT t1.assignment_id assignmentId, t1.person_id personId, t1.organization_id organizationId, t1.position_id positionId, t1.job_id jobId, t1.source_organization_id sourceOrganizationId, t1.effective_start_date effectiveStartDate, t1.effective_end_date effectiveEndDate, t1.grade_id gradeId, t1.primary_flag primaryFlag, t1.assignment_sequence assignmentSequence, t1.assignment_number assignmentNumber, t1.staff_post_name staffPostName, t1.payroll payroll, t1.assignment_type assignmentType, t1.assignment_category assignmentCategory, t1.staff_category staffCategory, t1.leader_id leaderId, t1.leader_num leaderNum, t1.employer_name employerName, t1.tax_place taxPlace, t1.pay_of_social_security_place payOfSocialSecurityPlace, t1.create_by createBy, t1.create_date createDate, t1.update_by updateBy, t1.update_date updateDate, t1.attribute1, t1.attribute2, t1.attribute3, t1.attribute4, t1.attribute5, t1.attribute6, t1.attribute7, t1.attribute8, t1.attribute9, t1.attribute10, t1.synchronization_state synchronizationState, t1.people_group_id peopleGroupId, t1.company_name companyName, t1.people_group peopleGroup, t1.assignment_status_type_id assignmentStatusTypeId, t1.cuc_org_min_cost cucOrgMinCost, t1.cuc_org_min_per_type cucOrgMinPerType FROM ehrbase_assignments_all t1 WHERE t1.effective_start_date <= NOW() AND t1.effective_end_date >= NOW() AND t1.primary_flag = 'Y'
and t1.person_id = #{personId}
and 1=2

select date_format(t1.job_start_date,'%Y-%m-%d') jobStartDate, date_format(t1.job_end_date,'%Y-%m-%d') jobEndDate, t1.previous_employer_name previousEmployerName, t1.previous_department_name previousDepartmentName, t1.job_duty_name jobDutyName, ( case t1.company_internal_flag when 'Y' then '是' when 'N' then '否' else '' end ) companyInternalFlag, (select meaning from hades_lookups where lookup_type = 'CNC_INDUSTRY' and lookup_code =t1.working_of_industry ) workingOfIndustry, (select meaning from hades_lookups where lookup_type = 'CNC_ENGAGEMENT' and lookup_code =t1.previous_profession ) previousProfession from EHRBASE_EMP_PREVIOUS_JOBS t1 where 1=1
and t1.person_id = #{personId}
and 1=2

SELECT t1.qualification_awarded_org qualificationAwardedOrg, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_GZMC_1109' AND lookup_code = t1.qualification_awarded_name ) qualificationAwardedName, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_SKILL_LEVEL' AND lookup_code = t1.grade_attained ) gradeAttained, ( CASE t1.major_qualification_flag WHEN 'Y' THEN '是' WHEN 'N' THEN '否' ELSE '' END ) majorQualificationFlag, t1.qualification_awarded_comments qualificationAwardedComments FROM ehrbase_EMP_QUALIFICATIONS t1 WHERE 1 = 1
and t1.person_id = #{personId}
and 1=2

SELECT ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_ZYJSZGXL_1109' AND lookup_code = t1.qualification_category ) qualificationCategory, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_ZYJSZGMC_1109' AND lookup_code = t1.qualification_name ) qualificationName, DATE_FORMAT( t1.qualification_start_date, '%Y-%m-%d' ) qualificationStartDate, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CN_TECH_WAY' AND lookup_code = t1.qualification_channel ) qualificationChannel FROM ehrbase_emp_tech_qualification t1 where 1=1
and t1.person_id = #{personId}
and 1=2

SELECT DATE_FORMAT( t1.approval_date, '%Y-%m-%d' ) approvalDate, t1.reward_name rewardName, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'TYPEITEM_CNC_AWARD_TYPE' AND lookup_code = t1.reward_type ) rewardType, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'TYPEITEM_CNC_AWARD_TYPE' AND lookup_code = t1.reward_level ) rewardLevel, t1.rewarded_org_name rewardedOrgName, t1.reward_amount rewardAmount FROM ehrbase_emp_reward t1 where 1 = 1
and t1.person_id = #{personId}
and 1=2

SELECT t1.punishment_name punishmentName, DATE_FORMAT( t1.punished_date, '%Y-%m-%d' ) punishedDate, t1.punishment_type punishmentType, t1.punished_reason punishedReason, t1.org_of_punished orgOfPunished FROM ehrbase_emp_punishment t1 where 1=1
and t1.person_id = #{personId}
and 1=2

SELECT DATE_FORMAT( t1.join_start_date, '%Y-%m-%d' ) joinStartDate, DATE_FORMAT( t1.join_end_date, '%Y-%m-%d' ) joinEndDate, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CN_PARTY_TYPE' AND lookup_code = t1.politics_landscape_name ) politicsLandscapeName, DATE_FORMAT( t1.join_party_date, '%Y-%m-%d' ) joinPartyDate, t1.org_of_partisan orgOfPartisan, t1.duty_of_partisan orgOfPartisan FROM ehrbase_EMP_POLITICS_LANDSCAPE t1 where 1 = 1
and t1.person_id = #{personId}
and 1=2

SELECT ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_ZYJSZGXL_1109' AND lookup_code = t1.appoint_category ) appointCategory, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_ZYJSZGMC_1109' AND lookup_code = t1.appoint_name ) appointName, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_ZYJSZGDJ' AND lookup_code = t1.appoint_level ) appointLevel, t1.approval_unit approvalUnit, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_APP_CIRCS' AND lookup_code = t1.appoint_comments ) appointComments FROM ehrbase_emp_tech_appoint t1 where 1=1
and t1.person_id = #{personId}
and 1=2

SELECT ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_WORK_TYPE' AND lookup_code = t1.qualification_awarded_name ) qualificationAwardedName, ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_SKILL_LEVEL' AND lookup_code = t1.grade_attained ) gradeAttained, DATE_FORMAT( t1.qualification_awarded_date, '%Y-%m-%d' ) qualificationAwardedDate, DATE_FORMAT( t1.qualification_end_date, '%Y-%m-%d' ) qualificationEndDate, t1.qualification_certificate_num qualificationCertificateNum FROM ehrbase_emp_qualifications_app t1 where 1 = 1
and t1.person_id = #{personId}
and 1=2

SELECT /* t1.create_by createBy, t1.update_by updateBy, DATE_FORMAT(t1.create_date, '%Y-%m-%d %H:%i:%s') createDate, DATE_FORMAT(t1.update_date, '%Y-%m-%d %H:%i:%s') updateDate, */ DATE_FORMAT( t1.effective_start_date, '%Y-%m-%d' ) effectiveStartDate, DATE_FORMAT( t1.effective_end_date, '%Y-%m-%d' ) effectiveEndDate, DATE_FORMAT( Now(), '%Y-%m-%d' ) currentSysDate, t1.person_id personId, /*主键*/ t1.org_id orgId, /*组织主键*/ t1.full_name fullName, /*姓名*/ t1.employee_number employeeNumber, /*员工编号*/ t1.disability_flag disabilityFlag, /*是否残疾#值*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'YES_NO' AND lookup_code = t1.disability_flag ) disabilityFlagName, /*是否残疾*/ t1.immigrated_foreign_flag immigratedForeignFlag, /*是移居国外#值*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'YES_NO' AND lookup_code = t1.immigrated_foreign_flag ) immigratedForeignFlagName, /*是否残疾*/ t1.name_of_letters nameOfLetters, /*全拼*/ t1.user_person_type userPersonType, /*用工类型*/ t1.national_identifier nationalIdentifier, /*身份证号*/ DATE_FORMAT( t1.date_of_birth, '%Y-%m-%d' ) AS dateOfBirth, /*出生日期*/ CASE WHEN t1.sex = 'M' THEN '男' WHEN t1.sex = 'F' THEN '女' ELSE '' END AS sexName, /*性别*/ t1.sex sex , /*性别#值*/ t1.blood_type bloodType, /*血型#值*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'BLOOD_TYPE' AND lookup_code = t1.blood_type ) bloodTypeName, /*血型*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'NATIONALITY' AND lookup_code = t1.nationality ) nationalityName, /* 国籍*/ t1.nationality nationality, /* 国籍#值*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CN_RACE' AND lookup_code = t1.nation ) nationName, /* 民族*/ t1.nation nation, /* 民族#值*/ t1.town_of_birth townOfBirth, /*籍贯*/ t1.region_of_birth regionOfBirth, /* 出生地*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CN_HUKOU_TYPE' AND lookup_code = t1.household_register_type ) householdRegisterTypeName, /*户口类型*/ t1.household_register_type householdRegisterType, /* 户口类型#值*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CN_HUKOU_LOCN' AND lookup_code = t1.household_register_place ) householdRegisterPlaceName, /*户口所在地*/ t1.household_register_place householdRegisterPlace, /*户口所在地#值*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'MAR_STATUS' AND lookup_code = t1.marital_status ) maritalStatusName, /*婚姻状况*/ t1.marital_status maritalStatus, /*婚姻状况#值*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CN_HEALTH_STATUS' AND lookup_code = t1.state_of_health ) stateOfHealthName, /*健康状况*/ t1.state_of_health stateOfHealth, /*健康状况#值*/ t1.email_address emailAddress, /*邮箱*/ t1.social_security_num socialSecurityNum, /*社会保障号*/ t1.initial_job_type initialJobType, /*首次参加工作方式#值*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_JOIN_WAY' AND lookup_code = t1.initial_job_type ) initialJobTypeName, /*首次参加工作方式*/ /*'' enjoyTaxPerfer,*/ DATE_FORMAT( t1.initial_job_start_date, '%Y-%m-%d' ) initialJobStartDate, /*参加工作日期*/ DATE_FORMAT( t1.join_cuc_date, '%Y-%m-%d' ) joinCucDate,/*加入本企业日期*/ concat(t1.join_cuc_channel,'--',t1.join_cuc_other_channel) joinCucChannel /*加入本企业途径(主)*/, t1.join_cuc_other_channel joinCucOtherChannel /*加入本企业途径(子)*/, t1.specialties, /*特殊专长*/ DATE_FORMAT( t1.plan_of_retirement_date, '%Y-%m-%d' ) planOfRetirementDate, /*预计退休时间*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_OTHER_PER_TYPE' AND lookup_code = t1.other_user_person_type ) otherUserPersonTypeName, /*其他人员类型*/ t1.other_user_person_type otherUserPersonType,/*其他人员类型#值*/ t1.join_cuc_other_channel_new joinCucOtherChannelNew, /*加入本企业子途径（新）*/ t1.join_cuc_channel_new joinCucChannelNew, /*加入本企业途径（新）*/ t1.join_cuc_channel_mark_new joinCucChannelMarkNew, /*加入本企业途径说明（新）*/ DATE_FORMAT( t1.working_ages_start_date, '%Y-%m-%d' ) workingAgesStartDate, /*工龄计算开始日期*/ t1.interval_working_ages_month intervalWorkingAgesMonth, /*间断工作时间（月）*/ DATE_FORMAT( t1.join_office_date, '%Y-%m-%d' ) joinOfficeDate, /*入局时间*/ DATE_FORMAT( t1.add_staff_date, '%Y-%m-%d' ) addStaffDate, /*增员时间*/ ( SELECT meaning FROM hades_lookups WHERE lookup_type = 'CNC_ADD_WAY' AND lookup_code = t1.add_staff_type ) addStaffTypeName, /*增员方式*/ t1.add_staff_type addStaffType, /*增员方式#值*/ ( select meaning from hades_lookups where lookup_type = 'CNC_ADD_WAY_EXPL' AND lookup_code = t1.add_staff_comments) addStaffCommentsName, /*增员方式说明*/ t1.add_staff_comments addStaffComments, /*增员方式说明#值*/ t1.staff_sort_num staffSortNum, /*排序号*/ t1.ufida_system_number ufidaSystemNumber /*用友员工编号*/ FROM ehrbase_emp_basic t1 where 1 = 1
and t1.person_id = #{personId}
and 1=2

mapper.update:
update EHRBASE_EMP_BASIC
	set SYNCHRONIZATION_STATE = #{synchronizationState}
	where PERSON_ID = #{personId}

update ehrbase_emp_basic set update_by = #{updateBy}, full_name = #{fullName}, name_of_letters = #{nameOfLetters}, national_identifier = #{nationalIdentifier}, sex = #{sex}, date_of_birth = #{dateOfBirth}, region_of_birth = #{regionOfBirth}, nationality = #{nationality}, town_of_birth = #{townOfBirth}, nation = #{nation}, marital_status = #{maritalStatus}, household_register_type = #{householdRegisterType}, household_register_place = #{householdRegisterPlace}, blood_type = #{bloodType}, state_of_health = #{stateOfHealth}, disability_flag = #{disabilityFlag}, immigrated_foreign_flag = #{immigratedForeignFlag}, social_security_num = #{socialSecurityNum}, plan_of_retirement_date = #{planOfRetirementDate}, other_user_person_type = #{otherUserPersonType}, email_address = #{emailAddress}, initial_job_start_date = #{initialJobStartDate}, initial_job_type = #{initialJobType}, working_ages_start_date = #{workingAgesStartDate}, interval_working_ages_month = #{intervalWorkingAgesMonth}, join_cuc_date = #{joinCucDate}, join_cuc_channel = #{joinCucChannel}, staff_sort_num = #{staffSortNum}, ufida_system_number = #{ufidaSystemNumber}, specialties = #{specialties}, join_office_date = #{joinOfficeDate}, add_staff_date = #{addStaffDate}, add_staff_type = #{addStaffType}, add_staff_comments = #{addStaffComments}, join_cuc_channel_new = #{joinCucChannelNew}, join_cuc_other_channel_new = #{joinCucOtherChannelNew}, join_cuc_channel_mark_new = #{joinCucChannelMarkNew} where 1=1
and person_id = #{personId}
and 1=2
